import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import axios from 'axios';

import Button from 'react-bootstrap/Button'

const Room = props => (
    <tr>
      <td>{props.room.roomName}</td>
      <td>{props.room.status}</td>
      <td>
        <Link to={"/editRoom/"+props.room._id}>edit</Link> | <a href="#" onClick={() => { props.deleteRoom(props.room._id) }}>delete</a>
      </td>
    </tr>
  )

export default class RoomList extends Component {


    constructor(props) {
        super(props);

        this.deleteRoom = this.deleteRoom.bind(this);

        this.state = { rooms: []}
    }
    componentDidMount() {
        axios.get('http://localhost:3000/rooms/')
        .then(response => {
            this.setState({ rooms: response.data })
        })
        .catch((error) => {
            console.log(error)
        })
    }
    deleteMessage(id) {
        axios.delete('http://localhost:3000/rooms/'+id)
          .then(response => { console.log(response.data)});

        this.setState({
          reooms: this.state.messages.filter(el => el._id !== id)
        })
      }

      roomList() {
        return this.state.room.map(currentmessages => {
          return <Message message={currentmessages} deleteMessage={this.deleteMessage} key={currentmessages._id}/>;
        })
      }

    render() {
        return (
            <div>
            <h3>Room History</h3>
            <Link to="/createRoom" className="nav-link">
            <Button variant="primary" size="lg" block>
            Create New Chat Room
          </Button>
          </Link>
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Chat Room Name</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                { this.messageList() }
              </tbody>
            </table>
          </div>
        )
    }

}
