import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import axios from 'axios';

const Exercise = props => (
    <tr>
      <td>{props.exercise.username}</td>
      <td>{props.exercise.description}</td>
      <td>{props.exercise.duration}</td>
      <td>{props.exercise.date.substring(0,10)}</td>
      <td>
        <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
      </td>
    </tr>
  )



export default class EventlogList extends Component {


    constructor(props) {
        super(props);

        this.deleteEvent = this.deleteEvent.bind(this);

        this.state = { events: []}
    }
    componentDidMount() {
        axios.get('http://localhost:3000/events/')
        .then(response => {
            this.setState({ events: response.data })
        })
        .catch((error) => {
            console.log(error)
        })
    }
    deleteEvent(id) {
        axios.delete('http://localhost:3000/events/'+id)
          .then(response => { console.log(response.data)});

        this.setState({
          events: this.state.exercises.filter(el => el._id !== id)
        })
      }

      eventList() {
        return this.state.events.map(currentevents => {
          return <Event event={currentevents} deleteExercise={this.deleteEvent} key={currentevents._id}/>;
        })
      }

    render() {
        return (
            <div>
            <h3>Logged Event History</h3>
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th></th>
                  <th>Event Type</th>
                  <th>Date Of Event</th>
                  <th>Event Description</th>
                  <th>User</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                { this.exerciseList() }
              </tbody>
            </table>
          </div>
        )
    }

}
