import React, { Component } from 'react';

import axios from 'axios';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class EditEventlog extends Component {

    constructor(props) {
        super(props);

        this.onChangeType = this.onChangeType.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeEvent = this.onChangeEvent.bind(this);
        this.onChangeUser = this.onChangeUser.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {

            type: '',
            event: '',
            user: '',
            date: new Date(),

        }
    }
    componentDidMount() {
        axios.get('http://localhost:3000/events/'+this.props.match.params.id)
          .then(response => {
            this.setState({
              type: response.data.type,
              event: response.data.event,
              user: response.data.user,
              date: new Date(response.data.date),
            })   
          })
          .catch(function (error) {
            console.log(error);
          })
      }
    onChangeType(e) {
        this.setState({
            type: e.target.value
        });
    }
    onChangeEvent(e) {
        this.setState({
            event: e.target.value
        })
    }
    onChangeUser(e) {
        this.setState({
            user: e.target.value
        })
    }
    onChangeDate(date) {
        this.setState({
            date: date
        });
    }
    onSubmit(e)
    {
        e.preventDefault();

        const event = {
            type: this.state.type,
            date: this.state.date,
            event: this.state.event,
            user: this.state.user
        }
        console.log(event);

        axios.post('http://localhost:3000/events/update/'+ this.props.match.params.id, event)
        .then(res => console.log(res.data));

        window.Location.href = '/';
    }


    render() {
        return (
            <div>
            <h3>Edit Event Log</h3>
            <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
                <label>Type: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.type}
                    onChange={this.onChangeType}
                    />
              </div>
              <div className="form-group"> 
              <label>Event: </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.event}
                  onChange={this.onChangeEvent}
                  />
            </div>
            <div className="form-group"> 
            <label>User: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.user}
                onChange={this.onChangeUser}
                />
          </div>



              <div className="form-group">
                <label>Date: </label>
                <div>
                  <DatePicker
                    selected={this.state.date}
                    onChange={this.onChangeDate}
                  />
                </div>
              </div>

              <div className="form-group">
                <input type="submit" value="Edit Event Log" className="btn btn-primary" />
              </div>
            </form>
          </div>
        )
    }

}
