import React, { Component } from 'react';


export default class CreateMessage extends Component {

    constructor(props) {
        super(props);

        this.onChangeMessage = this.onChangeMessage.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangetimestamp = this.onChangetimestamp.bind(this);
        this.onChangeRoom = this.onChangeRoom.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {

            msg: '',
            by: '',
            timestamp: new Date(),
            room: ''

        }
    }
    onChangeMessage(e) {
        this.setState({
            msg: e.target.value
        });
    }
    onChangeAuthor(e) {
        this.setState({
            by: e.target.value
        });
    }
    onChangetimestamp(date) {
        this.setState({
            timestamp: date
        });
    }
    onChangeRoom(e) {
        this.setState({
            room: e.target.value
        });
    }
    onSubmit(e)
    {
        e.preventDefault();

        const message = {
            msg: this.state.msg,
            by: this.state.by,
            timestamp: this.state.date,
            room: this.state.room
        }
        console.log(message)

        window.location = '/'
    }

    render() {
        return (
            <div>
                <p>You are on Create Message page</p>
            </div>
        )
    }

}
